bool Graph::setValue(char val, int i, int j) {
    if(_adjMatrix[i][j] > 1){return false;}
    if(i!=j && _adjMatrix[i][j] == 0){return false;}
    _adjMatrix[i][j] = val;
    _adjMatrix[j][i] = val;
    return true;
}

char Graph::getValue(int i, int j) {
    return _adjMatrix[i][j];
}
